(128, 'biblio', 'O:8:"stdClass":83:{s:3:"nid";s:3:"128";s:3:"vid";s:3:"128";s:4:"type";s:6:"biblio";s:6:"status";s:1:"1";s:7:"created";s:10:"1216065090";s:7:"changed";s:10:"1218649789";s:7:"comment";s:1:"0";s:7:"promote";s:1:"0";s:6:"sticky";s:1:"0";s:18:"revision_timestamp";s:10:"1218649789";s:5:"title";s:114:"The "Ham and Eggs Movement" in Southern California: Public Opinion on Economic Redistribution in the 1938 Campaign";s:4:"body";s:0:"";s:6:"teaser";s:0:"";s:3:"log";s:0:"";s:6:"format";s:1:"1";s:3:"uid";s:1:"9";s:4:"name";s:5:"epenn";s:7:"picture";s:0:"";s:4:"data";s:24:"a:1:{s:7:"contact";i:1;}";s:11:"biblio_type";s:4:"1000";s:13:"biblio_number";s:0:"";s:14:"biblio_section";s:0:"";s:19:"biblio_other_number";s:0:"";s:22:"biblio_secondary_title";s:0:"";s:21:"biblio_tertiary_title";s:0:"";s:18:"biblio_short_title";s:0:"";s:22:"biblio_alternate_title";s:0:"";s:23:"biblio_translated_title";s:0:"";s:14:"biblio_authors";s:57:"Elizabeth Maggie Penn;R. Michael Alvarez;William Deverell";s:24:"biblio_secondary_authors";s:0:"";s:23:"biblio_tertiary_authors";s:0:"";s:18:"biblio_corp_author";s:0:"";s:32:"biblio_other_author_affiliations";s:0:"";s:14:"biblio_edition";s:0:"";s:16:"biblio_publisher";s:0:"";s:27:"biblio_original_publication";s:0:"";s:22:"biblio_reprint_edition";s:0:"";s:22:"biblio_place_published";s:0:"";s:11:"biblio_year";s:4:"2008";s:13:"biblio_volume";s:0:"";s:24:"biblio_number_of_volumes";s:0:"";s:12:"biblio_pages";s:0:"";s:11:"biblio_date";s:0:"";s:11:"biblio_isbn";s:0:"";s:11:"biblio_issn";s:0:"";s:11:"biblio_lang";s:0:"";s:13:"biblio_abst_e";s:889:"We examine an important case study of the controversial use of the initiative process in California: the colorful “Ham and Eggs” movement of 1938, a misguided attempt to provide elderly Californians with a weekly pension. Our analysis focuses on a collection of postcard survey responses recently discovered at the Huntington Library in Pasadena, California. This survey, conducted in 1938 by the Research Institute, posed questions\r\nabout a series of pressing issues, including the various Ham and Eggs initiatives. We provide a qualitative study of the open-ended responses provided by survey participants along with a quantitative analysis of the fixed-choice questions. As best as we are aware, this is the only survey data on California politics of this period. Thus our analysis, while\r\nnecessarily limited, sheds additional light on this important period of California politics.";s:13:"biblio_abst_f";s:0:"";s:16:"biblio_full_text";s:1:"0";s:15:"biblio_keywords";s:0:"";s:10:"biblio_url";s:0:"";s:10:"biblio_doi";s:0:"";s:12:"biblio_issue";s:0:"";s:19:"biblio_type_of_work";s:0:"";s:23:"biblio_accession_number";s:0:"";s:18:"biblio_call_number";s:0:"";s:12:"biblio_notes";s:0:"";s:12:"biblio_coins";s:419:"<span class="Z3988" title="ctx_ver=Z39.88-2004&amp;rft_val_fmt=info%3Aofi%2Ffmt%3Akev%3Amtx%3Adc&amp;rfr_id=info%3Asid%2Fscholar.iq.harvard.edu&amp;rft.title=The+%22Ham+and+Eggs+Movement%22+in+Southern+California%3A+Public+Opinion+on+Economic+Redistribution+in+the+1938+Campaign&amp;rft.date=2008&amp;rft.aulast=Penn&amp;rft.aufirst=Elizabeth+Maggie&amp;rft.au=Alvarez%2C+Michael&amp;rft.au=Deverell%2C+William"></span>";s:21:"biblio_research_notes";s:0:"";s:14:"biblio_custom1";s:0:"";s:14:"biblio_custom2";s:0:"";s:14:"biblio_custom3";s:0:"";s:14:"biblio_custom4";s:0:"";s:14:"biblio_custom5";s:0:"";s:14:"biblio_custom6";s:0:"";s:14:"biblio_custom7";s:0:"";s:19:"biblio_auth_address";s:0:"";s:21:"biblio_remote_db_name";s:0:"";s:25:"biblio_remote_db_provider";s:0:"";s:14:"biblio_citekey";s:3:"128";s:12:"biblio_label";s:0:"";s:18:"biblio_access_date";s:0:"";s:16:"biblio_type_name";s:13:"Working Paper";s:4:"path";s:100:"epenn/the-ham-and-eggs-movement-southern-california-public-opinion-economic-redistribution-1938-camp";s:9:"og_groups";a:1:{i:0;i:113;}s:14:"og_groups_both";a:1:{i:113;s:5:"epenn";}s:9:"og_public";b:1;s:4:"tags";a:1:{i:6;a:2:{i:59;O:8:"stdClass":5:{s:3:"tid";s:2:"59";s:3